INSERT INTO news(theme_image,short_description,title, body,created_at, modified_on, created_by, modified_by, banner_state,news_category) Values
('media/news/theme/img-news-1.jpg','Gas Price Increas....','Gas Price in 1/2023', N'Từ 1/1, mỗi bình gas loại 12 kg đến tay người tiêu dùng đội thêm 13.000 đồng, bật lên 438.000 đồng, do nhu cầu sử dụng nhiên liệu tăng. Người dùng sẽ phải trả thêm 1.083 đồng đồng cho mỗi kg gas (tương đương 13.000 đồng một bình 12 kg) so với tháng trước. Giá này đã bao gồm thuế giá trị gia tăng (VAT) khi mua tại các điểm bán lẻ ở TP HCM và các tỉnh phía Nam. Sau sáu tháng liên tiếp giá nhiên liệu này giảm sâu (từ tháng 5 đến tháng 10 giảm tổng cộng 111.000 đồng mỗi bình 12 kg), từ tháng 11, 12, nhiên liệu này bước vào mùa tăng giá dịp cao điểm sử dụng năng lượng cuối năm. Công ty Saigon Petro cho biết, giá gas tháng 12 tăng là do nhà cung cấp thế giới công bố giá nhiên liệu bình quân đạt 650 USD một tấn, tăng 40 USD so với tháng trước. Hiện giá gas trong nước phụ thuộc vào diễn biến thế giới với nguồn cung nội địa chủ động được khoảng 60% mức tiêu thụ. Nhiệt độ tại nhiều khu vực châu Âu, Bắc Mỹ và phía Bắc châu Á xuống thấp, nhu cầu sử dụng nhiên liệu tăng cao vào mùa đông khiến giá gas thế giới tiếp đà đi lên trong tháng 12.',
GETDATE(), GETDATE(), 1, 1, 0,'Daily'),
